import React from 'react'
import Post from "./Post/Post"

// const messageData = [
//   {
//     id: 1,
//     message: 'Hi, My name is Greg',
//     like: '15'
//   },
//   {
//     id: 2,
//     message: 'This is my first post',
//     like: '20'
//   },
// ]

const MyPosts = (props)=> {
  console.log(props.profilePage.newPostText)

    const newPostElement = React.createRef()

    const elementt = ()=> {
      const textPost = newPostElement.current.value
      props.addPost(textPost)
      newPostElement.current.value = ''
    }

     const onChengeElement = ()=> {
      const textPost = newPostElement.current.value
      props.updateNewPostText(textPost)
    }

    return <div className="">
        My posts

        <div>
          <textarea onChange={onChengeElement} ref={newPostElement} name="" id="" cols="30" rows="5">{props.profilePage.newPostText}</textarea>
          <button onClick={ elementt }>add post</button>
        </div>

        <div className="">
        New post
        </div>

        {props.profilePage.posts.map(item => <Post message={item.message} key={item.id} like={item.like} />)}
    </div>
}

export default MyPosts
