//     like: '15'
//   },
//   {
//     id: 2,
//     message: 'This is my first post',
//     like: '20'
//   },
// ]

const MyPosts = (props)=> {

    const newPostElement = React.createRef()
    const textPost = newPostElement.current.value
    const elementt = ()=> props.addPost(textPost)

  // debugger

    return <div className="">
        My posts

        <div>
          <textarea ref={newPostElement} name="" id="" cols="30" rows="10"></textarea>
          <button onClick={ elementt }>add post</button>
        </div>

        <div className="">
        New post
        </div>

        {props.posts.map(item => <Post message={item.message} key={item.id} like={item.like} />)}
    </div>
}

export default MyPosts
