const ADD_POST = 'ADD-POST'
const UPDATE_NEW_POST_TEXT = 'UPDATE-NEW-POST-TEXT'

const SEND_MESSAGE = 'SEND-MESSAGE'
const UPDATE_NEW_MESSAGE_BODY = 'UPDATE-NEW-MESSAGE-BODY'


const store = {
    _state: {
        profilePage: {
            posts: [
                {
                  id: 1,
                  message: 'Hi, My name is Greg',
                  like: '15'
                },
                {
                  id: 2,
                  message: 'This is my first post',
                  like: '20'
                }
            ],
            newPostText: ''
         },
         messagesPage: {
            dialogs: [
                {
                    id: 1,
                    name: 'Valera'
                },
                {
                    id: 2,
                    name: 'Mark'
                },
                {
                    id: 3,
                    name: 'Elena'
                }
            ],
            messages: [
                {
                    id: 1,
                    message: 'Hy My name is Petr'
                },
                {
                    id: 2,
                    message: 'MI dont belive youark'
                },
                {
                    id: 3,
                    message: 'Elena I dont belive you'
                }
            ],
            newMessageBody: ''
        }
    },
    _callSubscriber() {
        return ('state changed')
    },
    getState() {
        return this._state
    },
    subscribe(observer) {
        this._callSubscriber = observer
    },
    dispatch(action) {

        if (action.type === UPDATE_NEW_POST_TEXT) {
            this._state.profilePage.newPostText = action.text
            this._callSubscriber(this.getState())
        } else if (action.type === ADD_POST) {
            const newPost = {
                id: this._state.profilePage.posts.length+1,
                message: this._state.profilePage.newPostText,
                like: 0
            }
            this._state.profilePage.posts.push(newPost)
            this._state.profilePage.newPostText = ''
            this._callSubscriber(this.getState())
        } else if (action.type === sendMessageCreator) {
            let newPost = {
                id: this._state.messagesPage.messages.length+1,
                message: this._state.messagesPage.newMessageBody
            }
        } else if (action.type === updateMessageCreator) {
            this._state.messagesPage.newMessageBody = action.text
        }
    }
}



export const addPostActionCreate = ()=> ({type: ADD_POST})
export const onChengeElementActionCreate = (text)=> ({type: UPDATE_NEW_POST_TEXT, text})


export const sendMessageCreator = ()=> ({type: SEND_MESSAGE})
export const updateMessageCreator = (text)=> ({type: UPDATE_NEW_MESSAGE_BODY, text})


export default store
window.store = store
























